function Wc = myIntegration(T,Div,adjG,inputSelection);    stepLength = T/Div;   [x_1,y_1] = size(adjG);   [x_2,y_2] = size(inputSelection);      Wc = zeros(x_1,x_1);   b = zeros(x_1,1);   b(inputSelection) = ones(y_2,1);   B = diag(b);      expA = expm(adjG*stepLength);   expAn = expA;   for i = 1 : Div     halfMatrix = expAn * B;     Wc = Wc + stepLength * halfMatrix * halfMatrix';     expAn = expAn * expA;   end